/**
 * load properties file generated by maven-resources-plugin which contains
 * project version.
 */
$(function() {
	jQuery.i18n.properties({
		name : 'application',
		path : 'resources/properties/',
		mode : 'map',
		callback : function() {
			$('#application_version').html($.i18n.prop('application.version'));
		}
	});
});